	public static void main(String[] args) {
		//(1)创建源文件对象
		File src = new File("1.txt");
		
		//(2)创建目标文件对象
		File dest = new File("2.txt");
		
		//(3)创建输入流
		FileReader fr = null;
		//(4)创建输出流
		FileWriter fw = null;
		
		try {
			fr = new FileReader(src);
//			fw = new FileWriter(dest);//覆盖模式
			fw = new FileWriter(dest,true);//追加模式
			
			//(5)一边读一边写
			//从fr读一些，就写入fw一些
			char[] data = new char[6];//1024
			while(true){
				int len = fr.read(data);
				if(len == -1){
					break;
				}
				fw.write(data,0,len);//本次读了几个字符，就写几个字符
			}
		} catch (FileNotFoundException e) {
			e.printStackTrace();
		} catch (IOException e) {
			e.printStackTrace();
		} finally{
			try {
				if(fr!=null){
					fr.close();
				}
			} catch (IOException e) {
				e.printStackTrace();
			}
			
			try {
				if(fw!=null){
					fw.close();
				}
			} catch (IOException e) {
				e.printStackTrace();
			}
		}
	}

	public static void main(String[] args) {
		// (1)创建源文件对象
		File src = new File("2.jpeg");// 完整的描述：路径+文件名+后缀名

		// (2)创建目标文件对象
		File dest = new File("3.jpg");

		// (3)创建输入流
		// (4)创建输出流

		try (
				FileInputStream fis = new FileInputStream(src); 
				FileOutputStream fos = new FileOutputStream(dest);
			) {

			byte[] data = new byte[10];

			int len;
			while ((len = fis.read(data)) != -1) {
				fos.write(data, 0, len);
			}

		} catch (FileNotFoundException e) {
			e.printStackTrace();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	public long getDirLength(File dir){
		if(dir.isFile()){//如果是文件，直接返回文件的大小
			return dir.length();
		}else if(dir.isDirectory()){
			long sum = 0;
			File[] listFiles = dir.listFiles();
			for (File sub : listFiles) {
//				sum += 下一级的大小;
				sum += getDirLength(sub);
			}
			return sum;
		}
		return 0;//既不是文件又不是文件夹，不存在
	}

public void delDir(File file){		
	//如果是目录
	if(file.isDirectory()){
		//(2)先获取下一级，并删除下一级
		//a：获取下一级
		File[] listFiles = file.listFiles();
		//b：遍历并删除下一级
		for (File sub : listFiles) {
			//这是一个重复的过程
			delDir(sub);//调用自己
		}
	}
	
	//删除自己
	file.delete();
}
